def main(args: list = None) -> int:
    import logging
    import sys

    logging.basicConfig(format='%(levelname)s - %(message)s', level=logging.DEBUG)

    from mettle.genes import cli_run

    if args is None:
        args = sys.argv[1:]

    try:
        return cli_run(args)
    except Exception as ex:
        logging.error("%s: %s\n", type(ex).__name__, ex)
        raise
    finally:
        logging.shutdown()


def sql_build(args: list = None) -> int:
    import sys

    from mettle.genes import cli_sql_build

    return cli_sql_build(sys.argv[1:])
