class DBLock:
    """
    An object that contains a lock, timeout and retry information for row locking.
    """

    def __init__(self, mili_seconds: int, retrys: int):
        """
        Constructor, create the lock with the milisecond timeout and retry count.

        :param mili_seconds: Number of mili_seconds to wait between lock attempts.
        :param retrys: Number of retrys to make after each lock attempt.
        """
        self.mili_seconds = mili_seconds
        self.retrys       = retrys

        if self.retrys < 1:
            self.retrys = 1
