from mettle.db.statement import Statement as BaseStatement


class Statement(BaseStatement):
    """
    Encode.io Databases  overload.
    """

    def __init__(self, name: str, stmnt_type: int = 0):
        """
        Contructor.
        """
        BaseStatement.__init__(self, name, stmnt_type)
        self._fres = None
        self._fidx = 0


    def destroy(self):
        if self._fres:
            self._fres = None
