riot.tag2("ftl-breadcrumb",'<div class="ftl-breadcrumb {ftl-breadcrumb-numbered : opts.numbered}"> <a each="{link, index in opts.links}" href="{link.url}" class="{active : link.active}" onclick="{linkclicked}"> {link.text} <span if="{link.badge}" class="badge">{link.badge}</span> </a> </div>',"","",(function(i){var n=this;linkclicked=function(i){sellink=i.item.link,link={text:sellink.text,url:sellink.url},n.trigger("link-clicked",link)},n.on("before-mount",(function(){n.opts.links||(n.opts.links={links:[]})})),n.on("update",(function(i){i.links&&(n.opts.links=i.links)}))}));