riot.tag2("ftl-error-message",'<div show="{show}" ref="erros" class="container" style="margin-top:20px"> <div each="{message, idx in this.opts.messages}" no-reorder class="alert alert-{message.type} alert-dismissible col-md-10" style="margin-top:20px" role="alert"> <a href="#" class="close" data-dismiss="alert" aria-label="close">&times;</a> {message.msg} <ul each="{item, index in message.itens}"><li>{item.msg}</li></ul> <virtual if="{message.total > 0}"><ul>E mais {message.total} registro(s).</ul></virtual> </div> </div>',"","",(function(s){var e=this;e.on("mount",()=>{e.show=null!=e.opts.messages,e.update()}),this.hideMessage=function(s){e.show=!1,s.preventDefault()}.bind(this)}));