    def norm_c(self, chgvs: str):
        if not re.findall('NM|NR', chgvs):
            splits = chgvs.split(':')
            if splits:
                NM = self._hugo_to_nm(splits[0])
            else:
                raise ValueError('unsupported chr specification.')

            chgvs = NM + ':' + splits[1]

        return chgvs
